function jinghuafm() {
const {java, source} = this;	
function splitString(str) {
    const length = str.length;
    if (length < 15) {
        return length<9 ? [str] : [
            str.slice(0, 7),
            str.slice(7)
        ].filter(s => s);
    } else if (length < 20) {
        return [
            str.slice(0, 7),
            str.slice(7,13),
            str.slice(13)
        ].filter(s => s);
    }  else if (length < 24) {
        return [
            str.slice(0, 7),
            str.slice(7,13),
            str.slice(13,18),
            str.slice(18)
        ].filter(s => s);
    } else {
        return [
            str.slice(0, 7),
            str.slice(7, 13),
            str.slice(13, 18),
            str.slice(18, 22),
            str.slice(22)
        ].filter(s => s);
    }
}
