var data = [
    ["阅读模式", ""],
    ["多人对话", "tone_id=51"],
    ["真人发音", "tone_id=0"],
    ["甜美少女", "tone_id=1"],
    ["清亮青叔", "tone_id=2"],
    ["成熟大叔", "tone_id=4"],
    ["开朗青年", "tone_id=5"],
    ["温柔淑女", "tone_id=6"],
    ["清纯少女", "tone_id=12"],
    ["磁性青叔", "tone_id=17"],
];

// 设置保存接口
function set(num) {
    if (num < 0 || num >= data.length) {
        java.longToast("无效的接口编号！");
        return;
    }
    var show = "设置成功\n当前模式：";
    var tips = "";
    var msg = show + data[num][0] + tips;
    java.longToast(msg);
    var setData = {
        sz: num.toString(),
        tone: data[num][1]
    };
    source.setVariable(JSON.stringify(setData));
}

// 获取接口显示
function get() {
    var v = source.getVariable();
    try {
        var szStr = JSON.parse(v).sz;
        var sz = parseInt(szStr, 10);
        if (sz < 0 || sz >= data.length) {
            sz = 0;
        }
        var show = "当前模式：";
        java.longToast(show + data[sz][0]);
    } catch (e) {
        java.longToast("获取模式失败，使用默认模式。");
    }
}

function reg(is) {
 if (is) {
var v = source.getVariable();
try {
    var szStr = JSON.parse(v).sz;
    var sz = parseInt(szStr, 10);
    if (sz < 0 || sz >= data.length) {
        sz = 0;
    }
    set(sz);
} catch (e) {
    //java.longToast("初始化模式失败，使用默认模式。");
    sz = 0;
    set(sz);
    }
  }
}

