ruleBookInfo.intro:
@js:
t = book.getVariable("custom");
t = t != "" ? t: 0;
J = org.jsoup.Jsoup.parse(result);
site = J.select('.site');
list = "";
for (i = 0; i < site.length; ++i) {
  T = (x) => site[i].select(x).text();
  list += `〖${i}〗${T('a:eq(1)')}\n${T('a:eq(0)')}・${String(T('span')).replace(/更新\s*/, '')}\n&shy;\n`;
};
java.put("url", site[t].select('a:eq(1)').attr('href'));
`&nbsp;🌸当前线路〖${t}〗🌸\n&shy;\n` + list + J.select('#intro').html();

ruleToc.chapterList:
ol@a
@js:
J = result;
list = [];
for (i = 0; i < J.length; ++i) {
  u = J[i].attr('href');
  list.push({
    name: J[i].text(),
    url: u,
    num: u.split('/')[3].slice(0, -5)
  });
};
Rule = (a, b) => a.num - b.num;
list.sort(Rule);

