exploreUrl:
@js: 
个人中心 = 1

js = (cid, genre, gender) => `@js:
source.bookSourceUrl.split("#")[0] + "/reading/bookapi/new_category/landing/v/?" + [
    "category_id=${cid}",
    "limit=20",
    "offset=" + (page - 1),
    "gender=${gender}",
    "genre=${genre}"
  ].join("&")`

readHistory = (path) => `@js:
let ck = "sessionid=" + (String(cookie.getKey("fanqienovel.com", "sessionid")) ? String(cookie.getKey("fanqienovel.com", "sessionid")) : source.getLoginInfoMap()['手动登录Token'])
"/multi-detail?book_id=" + getBookId(java.ajax('https://reading.snssdk.com/reading/bookapi/read_history/list/v/?aid=1967,{"headers":{"Cookie":"' + ck + '"}}'), page-1)`

obj = (title, url, type, type1) => ({
  title: title,
  url: url,
  style: {
    layout_flexGrow: 1,
    layout_flexBasisPercent: type
  }
})

arr = []
java.longToast("请稍等，发现列表正在热更新！")
index=[0, 1, 2]
for (let i of index) {
  // java.toast(i)
  let type = i
  $ = JSON.parse(java.ajax(java.log(source.bookSourceUrl.split("#")[0] + "/reading/bookapi/new_category/front/v/?" + [
      "update_version_code=58932",
      "distinct_style=1",
      "new_category_tab=" + i
    ].join("&")
  ))).data.category_tab_data
  cate = []
  cate.push(obj(
    "====== " + $.tab_name + " ======", "", 1
  ))
  $.cell_data.forEach((c) => {
    gender_1 = (type == 0 || type == 1) ? type : 1;
    genre_1 = type == 3 ? 1 : type == 5 ? 110 : type == 6 ? 130 : 0;
    cate.push(obj(
      c.cell_name,
      js(c.atom_data[0].category_data.category_id, genre_1, gender_1),
      1
    ))
    for (j = 2; j < 5; j++) {
      c.atom_data.slice(1).forEach((a) => {
        d = a.category_data
        if (d.name.length == j) {
          cate.push(obj(d.name, js(d.category_id, genre_1, gender_1), -1))
        }
      })
    }
  })
  arr = cate.concat(arr)
}

let book_shelf_url = 'https://fanqienovel.com/reading/bookapi/bookshelf/info/v:version/?aid=1967&iid=0&version_code=57700&update_version_code=57700'
let book_shelf_info = JSON.parse(java.ajax(book_shelf_url + ', ' + JSON.stringify({
  headers: {
    'Cookie': "sessionid=" + (String(cookie.getKey("fanqienovel.com", "sessionid")) ? String(cookie.getKey("fanqienovel.com", "sessionid")) : (source.getLoginInfoMap() ? source.getLoginInfoMap() : {})['手动登录Token']),
  }
})))

java.log(JSON.stringify(book_shelf_info))

let username
if (book_shelf_info.code != 0) {
  个人中心 = 0
} else {
  let uinfo = java.ajax("https://fanqienovel.com/api/user/info/v2," + JSON.stringify({ headers: { Cookie: "sessionid=" + (String(cookie.getKey("fanqienovel.com", "sessionid")) ? String(cookie.getKey("fanqienovel.com", "sessionid")) : (source.getLoginInfoMap() ? source.getLoginInfoMap() : {})['手动登录Token']) }}))
  uinfo = JSON.parse(uinfo)
  username = uinfo.data.name
}

let gro = []
push = (title, url, type) => gro.push({
  title: title,
  url: url,
  style: {
    layout_flexGrow: 1,
    layout_flexBasisPercent: type
  }
});

let sArr = []

if (个人中心) {
  let groups_bookids = {
    "未分组": []
  }
  book_shelf_info.data.book_shelf_info.forEach(i => {
    if (!groups_bookids[i.group_name ? i.group_name : "未分组"]) groups_bookids[i.group_name] = []
    groups_bookids[i.group_name ? i.group_name : "未分组"].push(i.book_id)
  })

  Object.keys(groups_bookids).forEach(k => {
    var multi_detail_post_body = {
      data: {
        book_shelf_info: []
      }
    }
    groups_bookids[k].forEach(i => multi_detail_post_body.data.book_shelf_info.push({ book_id: String(i), item_id: '0' }))
    // var multi_detail_group_url = `https://fanqienovel.com/api/bookshelf/multidetail,${JSON.stringify(multi_detail_post_body)}`
    // var book_ids = getBookId(JSON.stringify(multi_detail_post_body)).join(",")
    push(k, "https://fanqienovel.com/fqbookshelf/groupName/" + k, 0.4)
  })
  if (Object.keys(groups_bookids).length % 2 != 0) push("占位", "", 0.4)
  // java.log(JSON.stringify(gro, null, 2))
  sArr.push(obj(username + '的个人中心', '', 1))
  // sArr.push(obj('首页推荐', "https://fanqienovel.com/tab/0", 0.4))
  // sArr.push(obj('猜你喜欢', "https://fanqienovel.com/tab/1", 0.4))
  // sArr.push(obj('猜你喜欢', "https://fanqienovel.com/tab/1", 0.4))
  sArr.push(obj("我的书架", "https://fanqienovel.com/fqbookshelf", 1))

  sArr = sArr.concat(gro)
  // java.toast(JSON.stringify(sArr)) 
  sArr.push(obj("阅读历史", readHistory(), 1))
}

arr = sArr.concat(arr)
JSON.stringify(arr)

