  	"title": title,
  	"url": url,
  	"style": {
   "layout_flexGrow": 1,
   "layout_flexBasisPercent": size
   }
 });

var url=source.key;
var fl=url+"/xclass/0/1.html";
var ph=url+"/ph/week.html";
var qb=url+"/quanben.html";
var html=java.ajax(fl);
var src=java.ajax(ph);
var qbl=java.ajax(qb);

if(html&&src&&qbl){
a=org.jsoup.Jsoup.parse(html).select(".sortChannel_nav a");
b=org.jsoup.Jsoup.parse(src).select(".sortChannel_nav a");
c=org.jsoup.Jsoup.parse(qbl).select(".sortChannel_nav a");
push("🌹分类🌹",null,1)
    for(i in a){
        title=a[i].text()
        url=a[i].attr("href")
        size=0.25;
  url=String(url).replace("1.html","{{page}}.html")
        push(title,url,size)
    };
 push("🌹排行🌹",null,1)
    for(i in b){
        title=b[i].text()
        url=b[i].attr("href")
  url=String(url).replace(".html","<,_{{page}}>.html")
        size=0.25;
        push(title,url,size)
    }

   push("🌹全本🌹",null,1)
    for(i in c){
        title=c[i].text()
        url=c[i].attr("href")
  url=String(url).replace(".html","<,_{{page}}>.html")
        size=0.25;
        push(title,url,size)
    }

}else{
  java.toast("发现列表获取失败！")
}

