push("随机", "/bk/getRandomBooks?c=20", 1, 1);

 category = (path) =>{  	
    	burl = source.getKey().match(/([^#]*)/)[1];
		 c_url = burl + path;		 
   return  JSON.parse(java.ajax(c_url)).data; 
		}		

		category("/bk/ch/getChannelList").map($=>{
			  id = $.id;
     	title = $.name;
     	if(id!=-100){
     		 push("📜" + title + "📜", null, 1, 1);
     		f_url =	 `/bk/ch/getChannelBook?channelId=${id}`;
     		 list = category(f_url);
     		 list.map(($,index) =>{
				     	id = $.tagPackage.id;
        	   title = $.tagPackage.name;
        	   url =	 `/bk/ch/getBookList?id=${id}&idCode=&tag=package&lastIdCode=&limit=1000`;
          if(list.length > 3&&list.length!=4){
		         if(index+1 <= list.length - list.length%3){	           
		       	    push(title, url, 1, 0.25);}
				      else{ push(title, url, 0, 0.29);}
			     	}
				else if(list.length == 4){ push(title, url, 1, 0.4);}
				else{ push(title, url, 1, 0.25);}

				});			
			}										        
