//       {"headers"{"Referer":baseUrl}}
function getContent(imgr,wdr,bsurl,isheads){
	wds=(wdr=="")?"":java.getString(wdr);
	tus=java.getString(imgr);
	var imgs="";
 heads=(isheads=="")?"":","+JSON.stringify(isheads)

	if(/@html/.test(imgr)){
		  	imgs=tus;
		}else{
			imgs=tus.split("\n");
			for(var i=0;i<imgs.length;i++){
				if(imgs[i]!=""){
				  imgs[i]='<img  src="'+
				        	bsurl+imgs[i]+heads+'" />'
				   }
				   java.log(imgs[i])
    }
			 imgs=imgs.join("\n")
		}
		return wds+imgs;
}

