bd = {
  "body": "s=1&p={{page}}",
  "headers": {
    "Referer": "{{baseUrl}}/",
    "X-Requested-With": "XMLHttpRequest"
  },
  "method": "POST"
};

sort = kind.map((name, index) => {
	index++; let  i = index,
	url = `s=${i}&p={{page}}`,
	title = name; bd.body = url;
	link = bu + JSON.stringify(bd);
	list = ({
    "title": title,
    "url": link,
    "style": {
      "layout_flexGrow": 1,
      "layout_flexBasisPercent": 0.25,
    }
  });
	return list
	});

JSON.stringify(sort);

