@js:
key = String(java.get('key'));
// str Âç≥‰∏∫inputËæìÂÖ•ÁöÑÊï∞ÊçÆ
function sliceCountStr(str) {
   let arr = []; // ËÆ∞ÂΩïÂàÜÂâ≤ËÆ°ÁÆóË°®ËææÂºè
   for (let i = 0; i < str.length; i++) {
      let item = str.charAt(i);
      let num = item;
      if (/[\d|\.]/.test(item)) {
          let j = i + 1;
          for (; j < str.length - 1; j++) {
            let otherItem = str.charAt(j);
            if (!/[\d|\.]/.test(otherItem)) {
              break;
            }
          }
          num = str.slice(i, j);
          i = j - 1;
          num = +num;
      }
      arr.push(num);
   }
   return arr;
}
// arr Âç≥‰∏∫‰∏ä‰∏ÄÊ≠•Â§ÑÁêÜÂ•ΩÁöÑÊï∞ÁªÑ
function countHandle(arr) {
      let charArr = [],
        numArr = [];
      for (let i = 0; i < arr.length; i++) {
        if (typeof arr[i] == 'number') {
          numArr.push(arr[i]);
        } else {
          if (charArr.length) {
            // Ê≠•È™§1
            // Â¶ÇÊûúÂΩìÂâçÁöÑËøêÁÆóÁ¨¶ÁöÑ‰ºòÂÖàÁ∫ßÊØîÊ†àÈ°∂ÁöÑ‰ºòÂÖàÁ∫ß‰ΩéÊàñÁõ∏Á≠âÔºåÂ∞±ËØ¥ÊòéÈúÄË¶ÅÊääÂâçÈù¢ÁöÑÂÄºÂÖ®ÈÉ®ËÆ°ÁÆóÂ•Ω
            // Â≠òÂÇ®ËøêÁÆóÁ¨¶ÁöÑÊ†àË¶Å‰∏ÄÁõ¥Âá∫Ê†àÔºåÁõ¥Âà∞Ê†à‰∏∫Á©∫ÊàñÂΩìÂâçÁöÑÂ≠óÁ¨¶ÁöÑ‰ºòÂÖàÁ∫ßÊØîÊ†àÈ°∂ÁöÑ‰ºòÂÖàÁ∫ßÈ´ò
            while (this.isPop(arr[i], charArr[charArr.length - 1])) {
              let t2 = numArr.pop();
              let t1 = numArr.pop();
              let char = charArr.pop();
              this.handleCalculation(numArr, t1, t2, char);
            }
            // ÂΩìÂâçËøêÁÆóÁ¨¶‰∏∫Âè≥Êã¨Âè∑
            if (arr[i] == ')') {
              // ÂèñÊ†àÈ°∂ËøêÁÆóÁ¨¶
              let st = charArr[charArr.length - 1];
              // Ê≠•È™§2
              // ÈÅáÂà∞Âè≥Êã¨Âè∑‰πüË¶Å‰∏ÄÁõ¥Âá∫Ê†àÔºåÁõ¥Âà∞ÈÅáÂà∞Â∑¶Êã¨Âè∑
              while (st != '(') {
                let t1, t2;
                let char = charArr.pop();
                if (char != '(') {
                  t2 = numArr.pop();
                  t1 = numArr.pop();
                  this.handleCalculation(numArr, t1, t2, char);
                }
                st = char;
              }
            }
            // ËøêÁÆóÁ¨¶‰∏ç‰∏∫Âè≥Êã¨Âè∑
            if (arr[i] != ')') {
              charArr.push(arr[i]);
            }
          } else {
            // Ê≠•È™§3
            // ËøêÁÆóÁ¨¶Ê†à‰∏∫Á©∫ÔºåÁõ¥Êé•ÂÖ•Ê†à
            charArr.push(arr[i]);
          }
        }
      }
      // Ê≠•È™§4
      // ÊúÄÂêéËøêÁÆóÁ¨¶Ê†àÂ¶ÇÊûúËøòÊúâÂ≠óÁ¨¶ÔºåË¶Å‰∏ÄÁõ¥Âá∫Ê†àÁõ¥Âà∞‰∏∫Á©∫
      while (charArr.length) {
        let t2 = numArr.pop();
        let t1 = numArr.pop();
        let char = charArr.pop();
        this.handleCalculation(numArr, t1, t2, char);
      }
   return numArr[0];
}
// Âü∫Êú¨Âä†Âáè‰πòÈô§ËøêÁÆóÂ§ÑÁêÜ
    function handleCalculation(numArr, num1, num2, char) {
      if (char == '+') {
        numArr.push(num1 + num2);
      } else if (char == '-') {
        numArr.push(num1 - num2);
      } else if (char == '*') {
        numArr.push(num1 * num2);
      } else if (char == '/') {
        numArr.push(num1 / num2);
      }
    }
    // Âà§Êñ≠ËøêÁÆóÁ¨¶ÁöÑ‰ºòÂÖàÁ∫ßÔºåÊòØÂê¶Âá∫Ê†àËøõË°åËÆ°ÁÆó
    function isPop(char1, char2) {
      // ËøêÁÆóÁ¨¶Ê†à‰∏∫Á©∫
      if (!char2) {
        return false;
      }
      // ËøêÁÆóÁ¨¶‰ºòÂÖàÁ∫ßÁõ∏Âêå
      if ((char1 == '+' || char1 == '-') && (char2 == '+' || char2 == '-')) {
        return true;
      }
      // ÂâçËÄÖËøêÁÆóÁ¨¶‰ºòÂÖàÁ∫ßÊØîÂêéËÄÖ‰Ωé
      if ((char1 == '+' || char1 == '-') && (char2 == '*' || char2 == '/')) {
        return true;
      }
      // ËøêÁÆóÁ¨¶‰ºòÂÖàÁ∫ßÁõ∏Âêå
      if ((char1 == '*' || char1 == '/') && (char2 == '*' || char2 == '/')) {
