@js:
list=[]
voList = Array.from(result).filter(n=>String(n).includes('<h3'))

ulList = Array.from(result).filter(n=>String(n).includes('<ul'))

ulList.map((n,index)=>{

   list.push({
	   href:"",
	   text:java.getString("text",voList[index]),
	   volume:true
  })
 dList = []

Array.from(org.jsoup.Jsoup.parse(n).select("li")).reverse().map(t=>{	      
	mList = Array.from(org.jsoup.Jsoup.parse(t).select(".muludiv")).reverse().map(k=>{

	 dList.push({
	   href:org.jsoup.Jsoup.parse(k).select("a")[0].attr("href"),
	   text:org.jsoup.Jsoup.parse(k).select("a")[0].text(),
	   volume:false
	 })
     })
   })

   if(java.getString("text",voList[index])!="番外篇"){
	  dList = dList.sort(function(a, b) {
            return parseInt((a.text.match(/\d+/) || [0])[0], 10) - parseInt((b.text.match(/\d+/) || [0])[0], 10)
        })
    }
    list = list.concat(dList)
})

