@js:
var kind = ["冒险","热血","都市",
"玄幻","悬疑","耽美","恋爱","生活",
"搞笑","穿越","修真","后宫","女主",
"古风","连载","完结"];
let bu = "/data/sort,"
bd = {
  "body": "s=1&p={{page}}",
  "headers": {
    "Referer": "{{baseUrl}}/",
    "X-Requested-With": "XMLHttpRequest"
  },
  "method": "POST"
};

sort = kind.map((name, i) => { i++
	let url = `s=${i}&p={{page}}`,
	title = name; bd.body = url; 
	link = bu + JSON.stringify(bd);
	list = ({
    "title": title,
    "url": link,
    "style": {
      "layout_flexGrow": 1,
      "layout_flexBasisPercent": 0.25,
    }
  });
	return list
	});

JSON.stringify(sort);

