@js:
var list = Array.from(result),
regex = /(\w+)\/$/,
id = baseUrl.match(regex)[1];
url = baseUrl.replace(regex, "morechapter");
try{
	p = java.post(url, `id=${id}`, 
	{Referer: baseUrl}).body();
	d = JSON.parse(p).data;
dir = d.map($ => ({
	text: $.chaptername,
	href: $.chapterid+".html"
	}) );
	}catch(e){
		java.log(e)
		dir = [];
		}
list.concat(dir).reverse()

