@js:
res = []

//筛选链接生成
function generateFilterUrl(new_values) {
  values = {
    order: 'lastupdate',
    anime: '0',
    tagid: '0',
    sortid: '0',
    typeid: '0',
    words: '0',
    rgroupid: '0',
    update: '0',
    isfull: '0',
    page: '{{page}}',
    initial: '0'
  }
  for (key in new_values) {
  	  values[key]=new_values[key]
  	}
  return source.bookSourceUrl + '/wenku/' + values.order + '_' + values.tagid + '_' + values.isfull + '_' + values.anime + '_' + values.rgroupid + '_' + values.sortid + '_' + values.typeid + '_' + values.words + '_' + values.page + '_' + values.update + '.html';
}

//书架
user = ajax(source.bookSourceUrl + "/user.php")
//java.toast(user)
try {
if (user.match(/<title>(登录|错误).+哔哩轻小说<\/title>/gi)) {
  //未登录
  res.push(
    {
      title: `>> 我的书架 | 未登录 <<`,
      url: '',
      style: { layout_flexGrow: 1, layout_flexBasisPercent: 1 }
    })
} else {
  //已登录
  res.push(
    {
      title: `>> 我的书架 | ${user.match(/<span class="user-name">.+<\/span>/gi)[0].replace(/<\/?span.*?>/gi,'')} <<`,
      url: '',
      style: { layout_flexGrow: 1, layout_flexBasisPercent: 1 }
    })
  bookcase = ajax(source.bookSourceUrl + "/bookcase.php")
  bookcase.match(/<select[\s\S]+?<\/select>/gi)[0].match(/<option.+<\/option>/gi).forEach(option => {
    res.push(
      {
        title: option.match(/>.+(?=<)/gi)[0].replace('>', ''),
        url: source.bookSourceUrl + "/bookcase.php?classid=" + option.match(/value=\".+?(?=\")/gi)[0].replace(/(value=\")/gi, ''),
        style: { layout_flexGrow: 1 }
      })
  })
}
} catch (e) {
  throw("书架加载失败：\n"+e+"\n登录页源码：\n"+user)
}

content = org.jsoup.Jsoup.parse(ajax(source.bookSourceUrl + "/wenku/"))
try {
content.select("ul#filters li.sort-li").forEach(li => {
  res.push({
    title: `>> ${li.select("h3").textNodes()[0].text()} <<`,
    url: "",
    style: {
      layout_flexGrow: 1,
      layout_flexBasisPercent: 1
    }
  })
  li.select("a").forEach(a => {
    let filter = {}
    let type = a.attr("data-filter-type")
    let value = a.attr("data-filter-value")
    filter[type]=value
    res.push({
      title: a.text(),
      url: generateFilterUrl(filter),
      style: { layout_flexGrow: 1 }
    })
  })
})
} catch (e) {
  throw("文库加载失败：\n"+e+"\n文库页源码：\n"+content)
}

JSON.stringify(res)
