h = el.innerHTML

expandedStyleSrc = src
styleJs = src.match(/<script>.+font.+<\/script>/gi)
if (styleJs) {
  styleJs = styleJs[0].replace(/<.?script>/gi,'')
  try {
    rEval = eval
    eval = function(script) {
      const expandLines = script.match(/[\n;][^\n;]+=\[[^\n;]*\.\.\.[^\n;]*\][\n;]/gi)
      if(expandLines) {
        expandLines.forEach(line => {
          l = line.replace(/[\n;]/gi, "")
          varName = l.split("=")[0]
          arrExpr = l.split("=")[1].replace(/^\[|\]$/gi, "").split(/,\s*/g)
          result = varName + "=[];"
          arrExpr.forEach(expr => {
            if (/^\.\.\./i.test(expr)) {
              result += varName + "=" + varName + ".concat(" + expr.replace(/^\.\.\./i, "") + ");"
            } else {
              result += varName + ".push(" + expr + ")"
            }
          })
          script = script.replace(line, "\n;" + result + ";\n")
        })
      }
      try {
        rEval(script)
      } catch(e) {
        throw("\n嵌套 Eval 执行错误："+e+"\n嵌套 js 内容："+script+"\n")
      }
    }

    function CSSStyleSheet() {
      this.raw=""
      this.replaceSync=function(rule) {
        this.raw=rule
      }
    }
    rEval(styleJs)
    eval=rEval
    document.adoptedStyleSheets.forEach(css => {
      expandedStyleSrc += "<style>" + css.raw + "</style>"
    })
  } catch (e) {
    java.log(src)
    throw("style js 执行错误："+e+"\njs 内容；\n"+styleJs)
  }
}

