var push = (title, url, size) => {
    if (!seenTitles.has(title)) {
        result.push({
            "title": title.trim(),
            "url": url,
            "style": {
                "layout_flexGrow": 1,
                "layout_flexBasisPercent": size
            }
        });
        seenTitles.add(title);
    }
};

// 获取总页数
var getMaxPage = function() {
    var firstPageUrl = "https://www.tuzac.com/all-tags?sort=4";
    var html = java.ajax(firstPageUrl);
    if (!html) return 1;

    var doc = org.jsoup.Jsoup.parse(html);
    var lastPageLink = doc.select("#pager a:contains(末页)").first();
    if (lastPageLink) {
        var href = lastPageLink.attr("href");
        var match = href.match(/\/all-tags\/(\d+)/);
        return match ? parseInt(match[1]) : 1;
    }
    return 1; // 安全回退
};

var maxPage = getMaxPage();
java.toast("🥰共检测到" + maxPage + "页分类🥰");
var start = new Date().getTime();
while (new Date().getTime() < start + 3000);
java.toast("😘内容过多,拼命加载中😘");

// 遍历所有页码
for (var page = 1; page <= maxPage; page++) {
    var pageUrl = page === 1 
        ? "https://www.tuzac.com/all-tags?sort=4" 
        : "https://www.tuzac.com/all-tags/" + page + "?sort=4";

    var html = java.ajax(pageUrl);
    if (!html) continue;

    var doc = org.jsoup.Jsoup.parse(html);
    var links = doc.select(".teaser-thumbnail a");

    for (var i = 0; i < links.size(); i++) {
        var a = links.get(i);
        var rawTitle = a.text();
        var url = a.attr("href") + "/{{page}}";
        push(rawTitle, url, 0.4);
    }
}

JSON.stringify(result);

