@js:
bookId = java.hexDecodeToString(result);
$ = JSON.parse(java.ajax(QDSign("v1/chapterlist/chapterlist", [
    "bookId=" + bookId
], "", false, true)))["Data"];
v_Names = {};
c_Array = [];
c_Json = {};


function b64Url(item_id) {
    return `data:chapterId;base64,${java.base64Encode(item_id)},{"type":"ywc"}`;
}


function push(vc, a, b, c, d, e, f) {
    let list = c_Json[vc];
    if(!list) c_Json[vc] = list = [];
    // if (!e==1 || false) return
    list.push({
        "ChapterName": a || "",
        "isVolume": b || false,
        "chapterUrl": c ? b64Url(
          JSON.stringify({
            source: "qd",
            cid: c || null,
            isVip: e==1 || false,
            isPay: f==1 || false,
            ccid: null,
            cbid: null
          })) : "",
        "ChapterInfo": d || "",
        "isVip": e==1 || false,
        "isPay": f==1 || false
    });
}


let t_List = $.Volumes;
for (let $$ of t_List) {
	  let Vc = $$.VolumeCode;
    v_Names[Vc] = $$.VolumeName;
    c_Json[Vc] = [];
    if (t_List.length > 1) {
        push(Vc, $$.VolumeName, true);
    }
}


t_List = $.Chapters.slice(1);
for (let $$ of t_List) {
    let c_name = v_Names[$$.Vc] || "未知分卷";
    let c_time = java.timeFormatUTC($$.T, 'yyyy-MM-dd HH:mm:ss', 12);
    let c_word = $$.W + "字";
    let c_info = [c_name, c_time, c_word].join("丨");
    push($$.Vc, $$.N, false, $$.C, c_info, $$.V, $$.P);
}


for(let i in c_Json){
c_Array = c_Array.concat(c_Json[i]);
}
c_Array;

