var fl=url+"/xs1.html";
var ph=url+"/top/";
var qb=url+"/quanben.html";
var html=java.ajax(fl);
var src=java.ajax(qb);

if(html&&src){
a=org.jsoup.Jsoup.parse(html).select(".sortChannel_nav a");
b=org.jsoup.Jsoup.parse(src).select(".sortChannel_nav a");
push("🌹分类🌹",null,1)
    for(i in a){
        title=a[i].text()
        url=a[i].attr("href")
        size=0.25;
  url=String(url).replace(".html","<,_{{page}}>.html")
        push(title,url,size)
    };
 push("🌹全本🌹",null,1)
    for(i in b){
        title=b[i].text()
        url=b[i].attr("href")
  url=String(url).replace(".html","<,_{{page}}>.html")
        size=0.25;
        push(title,url,size)
    }

}else{
