  let numArr = ["零","一","二","三","四","五","六","七","八","九"];
  let numLimit = ["亿","万",""];//设置数字上限
  let numUnit = ["千","百","十",""];
  //num四位数是一个阶段对应 numLimit 每个阶段对应一个 numUnit，每个数字对应numArr。
