@js:
   let result=[];
   let push=(title, url, size)=>result.push({
       "title": title,
       "url": url,
       "style": {
        "layout_flexGrow": 1,
        "layout_flexBasisPercent": size
       }
});
   bu=source.key;
   aj=java.ajax(bu);
   let a=org.jsoup.Jsoup.parse(aj);
   let b=a.select('.dropdown-menu li a');
   let c=a.select('.panel-default a');
   push('💖分类💖','',1);
   for (i in b) {
        let url=b[i].attr('href');
        url=url+"<,index_{{page}}.html>";
        push(b[i].text(), url, 0.25);
    }
   push('🏷️标签🏷️','',1);
   for (i in c) {
        let url=c[i].attr('href');
        url=String(url).replace(/\d.html/,'{{page-1}}.html');
        push(c[i].text(), url, 0.25);
    }
JSON.stringify(result);
