this.sethost = function() {
    try {
        if (hosts.length <= 1) {
            var config = JSON.parse(source.getVariable());
            if (!config || !Array.isArray(config) || config.length === 0) {
                config = [{}];
            }
            config[0].host = hosts[0];
            source.setVariable(JSON.stringify(config, null, "\t"));
            java.toast("当前服务器：" + hosts[0]);
            return;
        }

        var config = JSON.parse(source.getVariable());
        if (!config || !Array.isArray(config) || config.length === 0) {
            config = [{}];
        }

        var currentHost = config[0].host || hosts[0];
        var currentIndex = hosts.indexOf(currentHost);
        var newIndex = (currentIndex + 1) % hosts.length;
        var newHost = hosts[newIndex];

        config[0].host = newHost;
        source.setVariable(JSON.stringify(config, null, "\t"));
        java.toast("切换到：" + newHost);
    } catch(e) {
        source.setVariable(JSON.stringify([{host: hosts[0]}], null, "\t"));
          java.toast("已初始化：" + hosts[0]);
    }
};

for (var i = 0; i < sourceList.length; i++) {
    (function() {
        var s = sourceList[i];
        this[s.n] = function() {
            try {
                var config = JSON.parse(source.getVariable());
                if (!config || !Array.isArray(config) || config.length === 0) {
                    config = [{}];
                }
                config[0].source = s.v;
                source.setVariable(JSON.stringify(config, null, "\t"));
  java.toast("已切换" + (s.v ? '到' : '') + s.m);
            } catch(e) {
            	source.setVariable(JSON.stringify([{source: s.v}], null, "\t"));
  java.toast("已初始化" + (s.v ? '到' : '') + s.m);
            }
        };
    }).call(this);
}

