ruleContent.content:
$.data.content
<js>
(function(){
    const isVideo = "{{$.data.tab}}" === "video";
    if(!isVideo) return result;

    try {
        const content = result
            .replace(/\\+/g, '')
            .replace(/\s+/g, ' ')
            .match(/https?:\/\/\S+/)[0];

        if(typeof book !== 'undefined' && 
           typeof chapter !== 'undefined' &&
           book.durChapterIndex === chapter.index) {
            java.startBrowser(content, chapter.title || '视频播放');
            java.toast('视频加载中，请稍候...');
        }

        return `【📺刷新播放】视频地址：${content}`;
    } catch(e) {
        return result;
    }
})()
</js>

