@js:
(function() {
    try {
        var config = JSON.parse(source.getVariable());
        if (Array.isArray(config) && config.length > 0 && config[0].host) {
            baseUrl = config[0].host;
        }
    } catch(e) {}

    var sourceVal = '';
    try {
        var config = JSON.parse(source.getVariable());
        if (Array.isArray(config) && config.length > 0 && config[0].source) {
            sourceVal = config[0].source;
        }
    } catch(e) {}

    var param = '';
    if (sourceVal) {
        param = '&source=' +
encodeURIComponent(String(sourceVal));
    }

    return baseUrl + '/search?page={{page}}&key={{key}}' + param;
})();
if (key.length == 19 && !Number.isNaN(parseInt(key))) {
    u = `https://api5-normal-sinfonlineb.fqnovel.com/reading/bookapi/multi-detail/v/?aid=1967&iid=1&version_code=999&book_id=${key}`;
    kdy = java.base64Encode(u);
    nam = JSON.parse(java.ajax(u)).data[0].book_name;
    result = baseUrl + `/detail?url=${kdy}&source=番茄小说&name=${nam}`;
}

