@js:
function createRequest(id, path) {
	let path = path || "getthirdinfobysecondid";
  let url = `https://android.goreadnovels.com/categorysetajax/${path}.do`;
  let body = `id=${id}&page={{page}}&pagesize=10`;
  return `${url},{"body":"${body}","method":"POST"}`;
}

let ids = [12, 56, 57, 59, 110];
let ah = java.ajaxAll(ids.map(createRequest));

let list = [];

function push_list(title, url, flexBasisPercent) {
  list.push({
    title,
    url,
    style: {
      layout_flexGrow: "1",
      layout_flexBasisPercent: flexBasisPercent
    }
  });
}

function cy(page, flexBasisPercent) {
  JSON.parse(page).data.forEach(({ id, title }) => {
    push_list(title, createRequest(id, "getcategorydetailinfo"), flexBasisPercent);
  });
}

push_list("▱▱▱▱男生▱▱▱▱", null, "1");
cy(ah[0].body(), "0.25");

push_list("▱▱▱▱女生▱▱▱▱", null, "1");
cy(ah[1].body(), "0.25");

push_list("▱▱▱▱短篇▱▱▱▱", createRequest(57, "getcategorydetailinfo"), "1");
push_list("▱▱▱▱二次元▱▱▱▱", createRequest(59, "getcategorydetailinfo"), "1");
push_list("▱▱▱▱出版▱▱▱▱", createRequest(110, "getcategorydetailinfo"), "1");

JSON.stringify(list);

