@js:
let type = +(java.get('btype'))
java.log(type)
switch (type) {
  case 1:
    book.type = 64;
    break;
  case 4:
    book.type = 32;
    break;
  default:
    book.type = 8;
}

const list = [];

function b64Url(item_id) {
  const url = book.type === 32 
    ? `https://reading.snssdk.com/reading/reader/audio/playinfo/?tone_id=1&item_ids=${item_id}&pv_player=-1&aid=1967`
    : `data:;base64,${java.base64Encode(item_id)},{"type":"pyfqc"}`;
  return String(url);
}

const List = JSON.parse(result).data.chapterListWithVolume;
List.forEach($ => {
  $.forEach((_, i) => {
    const name = _.volume_name || "第一卷：默认";
    const time = java.timeFormat(_.firstPassTime * 1000);
    if (!i && book.type !== 32) list.push({ title: name, vo: true });
    list.push({ title: _.title, vo: false, itemid: b64Url(_.itemId), info: `${name} | ${time}` });
  });
});

list;

