var data = JSON.parse(result);
var volume = data.data.chapterListWithVolume;

var flattenChapters = function (chapters) {
    return chapters.flatMap(chapter => Array.isArray(chapter) ? flattenChapters(chapter) : chapter);
}

var list = flattenChapters(volume);
list.forEach(item => {
    const volumeName = Number(item.volume_name) === 0 ? "第一卷：默认" : item.volume_name;
    item.name = `${item.title}`;
    item.url = `https://fqnovel.ceseet.me/content?item_id=${item.itemId}`;
    item.info = `${volumeName} | ${java.timeFormat(item.firstPassTime * 1000)}`;
});

list;

