// 当前书源版本号，切勿修改，否则影响更新的识别
let localVersion = '4.7.13';


function login(flag) {
	if (flag == undefined) {
		result = JSON.parse(source.getLoginInfo())
	} else {
		putLoginInfo(JSON.stringify(result))
	}
	let base_url = getArgument('server')
	let zdyserver = String(result['自定义服务器(可不填)']);
	if (zdyserver.includes('http')) {
		setArgument('server', zdyserver);
		if (getKey(String(cookie.getCookie(base_url)))){
			let cookies=cookie.getCookie(base_url)
			removeCookie(base_url)
			cookie.setCookie(zdyserver,cookies)
		}
		java.toast(`\n\n当前服务器为自定义服务器\n${zdyserver}\n\n切换服务器请先清空服务器地址中的数据`);
	}
	let zdytone_id = String(result['听书Ai音色填写后点击右上角✔'] || '');
	if (zdytone_id) {
		setArgument('tone_id', zdytone_id);
	} else {
		setArgument('tone_id', '默认音色');
	}
	let plcolor = String(result['自定义评论颜色(可不填)'] || '#000000');
	if (plcolor) {
		setArgument('plcolor', plcolor);
	} else {
		setArgument('plcolor', '#000000');
	}
	let zdysources = String(result['自定义搜索源(多个用英文,分割)'] || '');
	if (zdysources) {
		setArgument('source', zdysources);
	};
	base_url = getArgument('server')
	let register_email = String(result['邮箱'])
	let password = String(result['密码'])
	let key = String(result['密钥']||'')

	//java.log(cookie.getCookie(base_url))
	if ((register_email && password || key)&& !String(getKey(String(cookie.getCookie(base_url))))) {
		removeCookie(base_url)
		let deviceKey = java.webView('', '', 'navigator.userAgent+window.screen.width+window.screen.height')
		if (String(deviceKey) == "undefined") {
			try {
				deviceKey = java.deviceID();
			} catch (e) {
				deviceKey = java.androidId();
			}
		}
		let deviceId = java.digestHex(deviceKey, "SHA256")
		java.longToast("正在登录中...")
		if (register_email && password){
			let options = JSON.stringify({
				method: 'POST',
				headers: {
					'Content-Type': 'application/json'
				},
				body: JSON.stringify({
					register_email: result['邮箱'],
					password: result['密码']
				})
			})
			try {
				let data = JSON.parse(java.ajax(`${base_url}/login_api,${options}`))
				if (data.code == 0) {
					java.toast("登录成功")
					cookie.setCookie(base_url, `qttoken=${data.key}; deviceId=${deviceId}`)
					result['密钥']=data.key
					putLoginInfo(JSON.stringify(result))
				} else {
					java.toast(data.msg || "登录失败，请重试！")
				}
			} catch (e) {
				java.toast("登录失败，请重试！\n" + e.message)
			}
		} else {
			cookie.setCookie(base_url, `qttoken=${key}; deviceId=${deviceId}`)
			let res=java.ajax(`${base_url}/user_api,{"method":"POST","headers":{"cookie":"${cookie.getCookie(base_url)}"}}`)
			try {
				res=JSON.parse(res)
				if (res.id!=undefined) {
					java.toast('密钥登录成功')
					result['邮箱'] = res.email
					putLoginInfo(JSON.stringify(result))
				} else {
					throw new Error()
				}
			} catch(e) {
				java.log(e)
				java.toast("登录失败")
			}
		}
	} else if (flag&&String(getKey(String(cookie.getCookie(base_url))))) {
		java.toast("当前已登录，请退出登录后重新登录");
		//checkStatus();
	}
}

function checkStatus() {
	let base_url = getArgument('server')
	let res=java.ajax(`${base_url}/user_api,{"method":"POST","headers":{"cookie":"${cookie.getCookie(base_url)}"}}`)
	try {
				res=JSON.parse(res)
				if (res.id!=undefined) {
					result['邮箱'] = res.email
					putLoginInfo(JSON.stringify(result))
					let devices
					try {
						devices = JSON.parse(res.device).length;
					} catch (e) {
						devices = res.device ? 1 : 0;
					}
					let isVip;
