//设置服务器
function set_server() {
	putLoginInfo(JSON.stringify(result))
	let zdyserver;
	let base_url = getArgument('server')
	try {
		zdyserver = String(result['自定义服务器(可不填)']);
		if (zdyserver.includes('http')) {
			setArgument('server', zdyserver);
			if (getKey(String(cookie.getCookie(base_url)))){
				let cookies=cookie.getCookie(base_url)
				removeCookie(base_url)
				cookie.setCookie(zdyserver,cookies)
			}
			java.toast(`\n\n当前服务器为自定义服务器\n${zdyserver}\n\n切换服务器请先清空服务器地址中的数据`);
		} else {
			zdyserver = '';
		}
	} catch (error) {
		zdyserver = '';
	}
	if (!zdyserver) {
		const servers = host
		const currentServer = getArgument('server') || '';
		const currentIndex = servers.indexOf(currentServer);

		const nextIndex = currentIndex >= 0 ? (currentIndex + 1) % servers.length : 0;
		const nextServer = servers[nextIndex];

		setArgument('server', nextServer);
		if (getKey(String(cookie.getCookie(currentServer)))){
			let cookies=cookie.getCookie(currentServer)
			removeCookie(currentServer)
			cookie.setCookie(nextServer,cookies)
		}
		java.toast(`\n\n当前服务器${nextIndex+1}:${nextServer}\n\n注意：切换服务器后需要在详情页刷新目录才能生效`);
	}
}

