//登录
function login() {
	  let url = getUrl();
   let cookies = this.cookie.getCookie(url) || this.java.getCookie(url)
   if (cookies && cookies != "") {
     java.toast("\n请不要重复登录，请先退出登录！")
     return false
   }
   try {
     java.startBrowserAwait(url, "登录")
   } catch (e) {
     java.toast(e)
   }
   cookies = cookie.getCookie(url)
   if (!cookies || cookies == "") {
     java.toast("登录失败！")
     return false
   }
   java.toast("登录成功！")
   return true
}

//退出登录
function logout() {
   cookie.removeCookie(getUrl());
   let cookies = cookie.getCookie(url)
   if (cookies && cookies != "") {
     java.toast("退出登录失败！")
     return false
   }
   java.toast("成功退出登录！")
   return true
}

