function login(b) {
   if (b == undefined) {
  	let x=source.getLoginInfoMap().get("页数")
  	v = String(source.getVariable()).split(",");
  	v[2]=x
  	source.setVariable(v.join(","));
  	return true;
  	}
  info = result
  so = getUrl();
  pw = info.密码

  if (b) {
    url = so + 'login,'
    body = `loginName=${info.邮箱}&password=${pw}&platform=`
  }else{
    	java.startBrowser('{{getUrl()}}','注册');
    }

   post = JSON.stringify({
    "body": String(body),
    "method": "POST"
  })
  url += post
  result = java.ajax(url)
  cache.put('uaaurls', result);
  //java.log(result)
  if(result.match(/success/))
    {
        p = String(result).replace(/.*nickName\"\:\"(.*?)\"?\,.*/, '$1')
        java.toast('\n'+'🧾' + p + '🧾' + '登录成功')
        java.log(url)
    c = cookie.getCookie(getUrl());
    source.putLoginHeader(c);
    }else if(result.match(/java\.net/)){
    	java.toast('\n❗登录失败，请换接口❗')}
}
