exploreUrl:
@js:
let url = "https://www.qidian.com/all/";
let html = java.get(url, {}).body();

if (/fffffffffffffffffff/.test(html)) {
  java.longToast("发现列表正在热更新…")
  java.webView(null, url, "document.cookie")
  html = java.get(url, {}).body();
}

let src = org.jsoup.Jsoup.parse(html);
let list = [];

let addListItem = (title, url, flexBasis) => {
  list.push({
    title,
    url,
    style: {
      "layout_flexGrow": "1",
      "layout_flexBasisPercent": flexBasis
    }
  });
};

// 处理分类链接
let processCategories = (elements, flexBasis) => {
  elements.forEach(element => {
    let title = element.text();
    let url = `https:${element.attr("href")}`;
    url = url.replace(/-?\/$/,"-page{{page}}/");
    addListItem(title, url, flexBasis);
  });
};

// 添加排行分类
let rankings = [
  ["▱▱▱▱排行▱▱▱▱", null],
  ["人气排行", "https://www.qidian.com/all/page{{page}}/"],
  ["总收藏", "https://www.qidian.com/all/orderId11-page{{page}}/"],
  ["总字数", "https://www.qidian.com/all/orderId3-page{{page}}/"],
  ["周推荐票", "https://www.qidian.com/all/page{{page}}/"],
  ["月推荐票", "https://www.qidian.com/all/page{{page}}/"],
  ["总推荐票", "https://www.qidian.com/all/page{{page}}/"]
];

rankings.forEach(([title, url]) => {
  addListItem(title, url, url ? "0.25" : "1");
});

// 添加免费分类
addListItem("▱▱▱▱免费▱▱▱▱", null, "1");
addListItem("连载", "https://www.qidian.com/all/action0-vip0-page{{page}}/", "0.25");
addListItem("完结", "https://www.qidian.com/all/action1-vip0-page{{page}}/", "0.25");

// 处理男生分类
addListItem("▱▱▱▱男生▱▱▱▱", null, "1");
let mainCategories = src.select(".type-filter").get(1).select("a");
processCategories(mainCategories.subList(1, mainCategories.size()), "0.25");

// 处理女生分类
addListItem("▱▱▱▱女生▱▱▱▱", null, "1");
mainCategories = src.select(".type-filter").get(0).select("a");
processCategories(mainCategories, "0.25");

// 处理出版分类
addListItem("▱▱▱▱出版▱▱▱▱", null, "1");
mainCategories = src.select(".type-filter").get(2).select("a");
processCategories(mainCategories, "0.25");

// 处理标签
addListItem("▱▱▱▱标签▱▱▱▱", null, "1");
let tags = src.select(".tag").select("a");
processCategories(tags, null);

JSON.stringify(list);

