<js>
var matches = result.match(/<dd data-id="(\d+)"[\s\S]*?<\/dd>/g) || [];
if (matches.length > 0){
    var elementsWithId = matches.map(function(element) {
        var match = element.match(/<dd data-id="(\d+)"/);
        return {
            element: element,
            id: parseInt(match[1])
        };
    });
    elementsWithId.sort(function(a, b) {
        return a.id - b.id;
    });
    var sortedElements = elementsWithId.map(function(item) {
        return item.element;
    });
    sortedElements.join('\n');
} else {
    result
}
</js>

